DROP TABLE IF EXISTS `coursestage`;

CREATE TABLE `coursestage` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `stageName` varchar(50) DEFAULT NULL,
  `thumbnail` varchar(500) DEFAULT NULL,
  `title1` varchar(50) DEFAULT NULL,
  `text1` text,
  `title2` varchar(50) DEFAULT NULL,
  `text2` text,
  `DATE` datetime DEFAULT NULL,
  `createuser` int(11) DEFAULT NULL,
  `state` int(11) DEFAULT '1',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COMMENT='课程阶段表';

/*Data for the table `coursestage` */

LOCK TABLES `coursestage` WRITE;

insert  into `coursestage`(`id`,`stageName`,`thumbnail`,`title1`,`text1`,`title2`,`text2`,`DATE`,`createuser`,`state`) values (1,'婴儿期','/public/course','好奇·探索','宝宝开始有了自我意识，喜欢探索周围的美好环境，有小小的模仿能力，而且已经开始认知因果关系，了解如何得到想要的结果，他们运用逐渐增强的技能和体能来感受周围世界。这些对于认知世界规律，发展沟通交流能力，培养竞争意识和自信心都非常重要。\r\n','模仿·辨别','通过“听觉、视觉”等多通道的刺激来提高婴幼儿挨打的无群多群无多去外地大青蛙请问去外地请问当前的认知能力驱蚊器翁群翁群翁，全方位促进\r\n','2017-12-20 20:22:09',1,1),(2,'幼儿期','/public/course','独立·沟通','本阶段的宝宝正处于在走路的敏感期。喜欢不停地运动，而宝宝的平衡能力、垫上运动能力、上肢力量和敏捷性是课程的发展重点，帮助宝宝从婴幼儿期就建立起对运动的参与热情。并且宝宝喜欢社会性游戏，开始学习分享与妥协，开始说简单的单字和词，会自己想办法解决一些问题。\r\n静品教育通过舞蹈环节和肢体练习，训练宝宝的次序性和方向感；加强语言和社交能力、空间感知；培养宝宝情绪调节与控制能力。帮助宝宝建立初期的认知和社交能力。','认可·选择','这个阶段是宝宝性格形成的关键期。宝宝在活动过程中，会运用自己掌握的技能不断尝试，经历失败后重新调整方式。在不断重复尝试之后，他们知道采取哪些行动，以怎样的顺序能达到目的;能控制跑得动作，有意识避开障碍物，有初步的是非观念，知道什么是对，什么是错。\r\n静品教育教宝宝在游戏中学习形状和顺序等基本概念，帮助宝宝了解世界，丰富有趣的课程内容和教学设备给宝宝带来更多的挑战，让宝宝在获得体能发展的同时，建立起良好的自尊。','2017-12-20 20:22:09',2,1),(3,'学前期','/public/course','认知·求知','这个阶段的宝宝表现出明显的个性特征，处于人生的第一个反抗期。宝宝的认知从感知阶段过度到象征认知阶段。初级运动技巧、精细动作和大动作的熟练程度、以及操作性技巧的练习成为这一阶段的学习重点。宝宝处于口头语言学习的关键期，正确的母语学习是最重要的。\r\n静品教育通过游戏建立了宝宝的符号联想能力；通过模仿和角色扮演、挑战性运动项目，培养宝宝观察力和事物的认识能力以及独立性和自主探索精神，建立良好的自信心和自尊心。','精细·认知·社交','这个阶段的宝宝表现出明显的个性特征，处于人生的第一个反抗期。宝宝的认知从感知阶段过度到象征认知阶段。初级运动技巧、精细动作和大动作的熟练程度、以及操作性技巧的练习成为这一阶段的学习重点。宝宝处于口头语言学习的关键期，正确的母语学习是最重要的。\r\n静品教育通过游戏建立了宝宝的符号联想能力；通过模仿和角色扮演、挑战性运动项目，培养宝宝观察力和事物的认识能力以及独立性和自主探索精神，建立良好的自信心和自尊心。','2017-12-20 20:22:09',3,1);

